  const [issues, setIssues] = useState({});

  const handleClick = (e) => {
    const dataName = e.currentTarget.getAttribute("data-name");
    if (!dataName) return;

    setSelected((prev) => {
      const newSet = new Set(prev);
      if (newSet.has(dataName)) {
        newSet.delete(dataName);

        setIssues((prevIssues) => {
          const updated = { ...prevIssues };
          delete updated[dataName];
          return updated;
        });
      } else {
        newSet.add(dataName);
      }
      return newSet;
    });
  };

